import { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import classes from "./Comments.module.css";
import NewCommentForm from "./NewCommentForm";
import { getAllComments } from "../../api/api";
import useHttp from "../../hooks/useHttp";
import LoadingSpinner from "../UI/ LoadingSpinner";
import CommenstList from "./CommentsList";
const Comments = () => {
  const { sendRequest, data: loadedComments, status, error } = useHttp(
    getAllComments
  );
  const [isAddingComment, setIsAddingComment] = useState(false);
  const params = useParams();
  const startAddCommentHandler = () => {
    setIsAddingComment(true);
  };

  const { quoteId } = params;
  const addedCommentsHandler = _ => {};
  useEffect(() => {
    sendRequest(quoteId);
  }, [quoteId, sendRequest]);

  let comments;
  if (status === "pending") {
    comments = (
      <div className='centerd'>
        <LoadingSpinner />
      </div>
    );
  }

  if (error) {
    return <p className='centerd focused'>{error}</p>;
  }

  if (status === "completed" && loadedComments && loadedComments.length > 0) {
    comments = <CommenstList comments={loadedComments} />;
  }

  if (
    status === "completed" &&
    (!loadedComments || loadedComments.length === 0)
  ) {
    return <NoQuotesFound />;
  }
  return (
    <section className={classes.comments}>
      <h2>User Comments</h2>
      {!isAddingComment && (
        <button className='btn' onClick={startAddCommentHandler}>
          Add a Comment
        </button>
      )}
      {isAddingComment && (
        <NewCommentForm
          quoteId={params.quoteId}
          onAddedComments={addedCommentsHandler}
        />
      )}
      <p>Comments...</p>
    </section>
  );
};

export default Comments;
